<?php function maskNumber($number, $decimals = 0, $prefix = '', $suffix = '') { return $prefix.number_format($number, $decimals, ',', '.').$suffix; } function fromNow(\Carbon\Carbon $date) { $now = \Carbon\Carbon::create(); return $date->diffForHumans(); } function ipAdminThemeUrl($path) { return ipConfig()->baseUrl().'ThemeAdmin/'.ipConfig()->get('adminTheme').$path; } function ipAdminThemeFile($path) { return ipFile( 'ThemeAdmin/' . ipConfig()->get('adminTheme') . '/' . $path ); } ?>