<?php namespace Ip; class Route { protected $action = null; protected $controller = null; protected $plugin = null; protected $environment = 'public'; protected $controllerClass = null; protected $name = null; protected $parameters = array(); const ENVIRONMENT_ADMIN = 'admin'; const ENVIRONMENT_PUBLIC = 'public'; public function plugin() { return $this->plugin; } public function parameters() { return $this->parameters; } public function parameter($parameterName, $default = null) { if (!empty($this->parameters[$parameterName])) { return $this->parameters[$parameterName]; } return null; } public function setPlugin($plugin) { $this->plugin = $plugin; } public function setParameters($parameters) { $this->parameters = $parameters; } public function controller() { return $this->controller; } public function controllerClass() { if ($this->controllerClass != null) { return $this->controllerClass; } if ($this->plugin == null) { return null; } if (in_array($this->plugin, \Ip\Internal\Plugins\Model::getModules())) { $controllerClass = 'Ip\\Internal\\' . $this->plugin . '\\' . $this->controller; } else { $controllerClass = 'Plugin\\' . $this->plugin . '\\' . $this->controller; } $this->controllerClass = $controllerClass; return $this->controllerClass; } public function setController($controller) { $this->controller = $controller; } public function action() { return $this->action; } public function setAction($action) { $this->action = $action; } public function environment() { return $this->environment; } public function setEnvironment($environment) { $this->environment = $environment; } public function isAdmin() { return $this->environment == self::ENVIRONMENT_ADMIN; } public function setName($name) { $this->name = $name; } public function name() { return $this->name; } } ?>